function translatePigLatin(str) {
  let strArr = str.split("");  
  let firstVowelIndex = strArr.findIndex(vowel => (vowel == 'a' || vowel == 'e' || vowel == 'i' || vowel == 'o' || vowel == 'u'));

  if (firstVowelIndex == -1) {
    return strArr.join("") + "ay";
  }

  if (firstVowelIndex > 0) {
    let removed = strArr.splice(0, firstVowelIndex);
    strArr = strArr.concat(removed);
    return strArr.join("") + "ay";
  } else {
    return strArr.join("") + "way";
  }
}
