function smallestCommons(arr) {

  //Find smallest and largest value in the given array "arr"
  arr.sort((a,b) => b-a);

  //Create an array "rangeArr" with all integers between smallest and largest
  let rangeArr = [];
  for (let i = arr[0]; i >= arr[1]; i--) {
    rangeArr.push(i);
  }

  //Calculate the smallest common multiple
  let count = 1;
  let multiple = rangeArr[0]*rangeArr[1]*count;

  while (true) {
    let flag = true;
    for (let i = 0; i < rangeArr.length; i++){
      if (multiple % rangeArr[i] != 0) {
        flag = false;
      }
    }
    if (flag == false) {
      multiple = rangeArr[0]*rangeArr[1]*count;
    } else {
      return multiple;
    }
    count++;
  }
}


