function rot13(str) { // LBH QVQ VG!
  let strArr = str.split("");

  for(let i = 0; i<strArr.length; i++){
    if(/[A-Z]/.test(strArr[i])){
      let decimal = strArr[i].charCodeAt(0) + 13;
      if (decimal > 90) {
        decimal -= 26;
      }
      strArr[i] = String.fromCharCode(decimal);
    }
  }
  return strArr.join("");
}

