function palindrome(str) {
  str = str.toLowerCase();
  str = str.replace(/[^a-z0-9]/g,"");
  let firstHalf = ""; 
  let lastHalf = "";

  if (str.length%2==1) {
    firstHalf = str.slice(0,(str.length-1)/2);
    lastHalf = str.slice(((str.length-1)/2)+1);
  } else {
    firstHalf = str.slice(0, str.length/2);
    lastHalf = str.slice(str.length/2);
  }

  let reverseLastHalf = lastHalf.split("").reverse().join("");

  if (firstHalf == reverseLastHalf) {
    return true;
  } else {
    return false;
  }
  return false;
}

