function convertToRoman(num) {
  let romanNumeralArr = [];
  let remainder = 0;

  //Calculate number of thousands, hundreds, tens, and ones in the number "num"
  let thousands = Math.floor(num/1000);
  remainder = num%1000;
  let hundreds = Math.floor(remainder/100);
  remainder = remainder%100;
  let tens = Math.floor(remainder/10);
  remainder = remainder%10;
  let ones = remainder;

  function addRomanNumerals(value, single, mid, end) {
    if (value>= 1 && value<=3) {
      for (let i = 0; i < value; i++) {
        romanNumeralArr.push(single)
      }
    } else if (value == 4) {
      romanNumeralArr.push(single + mid);
    } else if (value >= 5 && value <= 8) {
      romanNumeralArr.push(mid);
      for (let i = 5; i < value; i++) {
        romanNumeralArr.push(single);
      }
    } else if (value == 9) {
      romanNumeralArr.push(single + end);
    }
  }

  // Add thousands to the array
  for (let i = 0; i < thousands; i++) {
    romanNumeralArr.push("M");
  }
  // Add hundreds, tens and ones to the array
  addRomanNumerals(hundreds, "C", "D", "M");
  addRomanNumerals(tens, "X", "L", "C");
  addRomanNumerals(ones, "I", "V", "X");

  return romanNumeralArr.join("");
}

convertToRoman(36);
